import React, { useEffect, useState } from "react";
import axios from "axios";
import { Doughnut } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";

ChartJS.register(ArcElement, Tooltip, Legend);

const Orders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3002/allOrders")
      .then(res => setOrders(res.data))
      .catch(err => console.error("Error fetching orders:", err));
  }, []);

  // Prepare chart data
  const stockCounts = orders.reduce((acc, order) => {
    acc[order.name] = (acc[order.name] || 0) + 1;
    return acc;
  }, {});

  const chartData = {
    labels: Object.keys(stockCounts),
    datasets: [
      {
        label: "Orders by Stock",
        data: Object.values(stockCounts),
        backgroundColor: [
          "#FF6384", "#36A2EB", "#FFCE56", "#4BC0C0",
          "#9966FF", "#FF9F40", "#C9CBCF", "#8DD17E", "#F67019"
        ],
        borderWidth: 1
      }
    ]
  };

  return (
    <div className="container py-4">
      <h2>Your Orders</h2>

      {orders.length === 0 ? (
        <div className="text-center mt-4">
          <p>You haven't placed any orders today</p>
          <a href="/" className="btn btn-primary">Get started</a>
        </div>
      ) : (
        <>
          <div className="my-4" style={{ maxWidth: 400, margin: "0 auto" }}>
            <Doughnut data={chartData} />
          </div>

          <table className="table mt-4">
            <thead>
              <tr>
                <th>Stock</th>
                <th>Qty</th>
                <th>Price</th>
                <th>Mode</th>
              </tr>
            </thead>
            <tbody>
              {orders.map(order => (
                <tr key={order._id}>
                  <td>{order.name}</td>
                  <td>{order.qty}</td>
                  <td>{order.price}</td>
                  <td>{order.mode}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
};

export default Orders;
